#!/bin/bash

# This script deploys AlphaSheets on a clean server. It:
# * installs dependencies
# * writes the various configuration files
# * deploys frontend via nginx
# * builds the backend container image

# Requirements:
# * the alphasheets-builds folder (if you see this file, you have it.)
# * Ubuntu 14.04 (trusty)

# Run this script from the directory it lives in.

THIS_DIR=`CWD`

###### Install dependencies ######
mkdir ~/.alphasheets-dependencies
cd ~/.alphasheets-dependencies

apt-get update

apt-get install -y  libboost-all-dev \
                    libzmq3-dev \
                    libtool \
                    pkg-config \
                    build-essential \
                    autoconf \
                    automake \ 
                    python-all-dev \
                    libpng-dev \
                    zlib1g-dev \
                    libfreetype6-dev \
                    python-dev 

apt-get install -y  tcl8.5 \
                    nginx \
                    tmux

# redis
wget http://download.redis.io/releases/redis-stable.tar.gz
tar xzf redis-stable.tar.gz

cd redis-stable
make
make test
make install

cd utils
./install_server.sh
cd ../..

# pip
curl -O https://bootstrap.pypa.io/get-pip.py
python get-pip.py
pip install six

# libsodium
git clone git://github.com/jedisct1/libsodium.git
cd libsodium 
./autogen.sh
./configure
make check
make install
ldconfig
cd ..

# zmq
git clone https://github.com/zeromq/zeromq4-1
cd zeromq4-1
./autogen.sh
./configure
make check
make install
ldconfig
cd ..

# R 
echo "deb http://cran.rstudio.com/bin/linux/ubuntu trusty/" >> /etc/apt/sources.list
gpg --keyserver keyserver.ubuntu.com --recv-key E084DAB9
gpg -a --export E084DAB9 | sudo apt-key add -
apt-get update
apt-get -y install r-base

# R packages
echo "r <- getOption('repos'); r['CRAN'] <- 'http://cran.us.r-project.org'; options(repos = r);" > ~/.Rprofile
Rscript -e "install.packages('rjson')"
Rscript -e "install.packages('ggplot2')"
Rscript -e "install.packages('party')"
Rscript -e "install.packages('jpeg')"

###### Write configurations ######

cd $THIS_DIR

# ssh
cd config
cp sshd_config /etc/ssh/sshd_config
service ssh restart

# redis
cp redis/redis_6379 /etc/init.d/
cp redis/6379.conf /etc/redis/
update-rc.d redis_6379 defaults
service redis_6379 restart

# nginx
cd nginx
cp nginx.conf /etc/nginx/nginx.conf
rm -rf /etc/nginx/sites-enabled
cp -r sites-enabled /etc/nginx/
service nginx restart

# authorize build server to login 
touch ~/.ssh/authorized_keys
echo "ssh-rsa  builds@alphasheets.com" >> ~/.ssh/authorized_keys
